Galleria.addTheme({
    name: 'Horiz',
    author: 'Mayes, Kennedy & Company, http://mayeskennedy.co.uk',
    version: 1,
    css: 'galleria.horiz.css',
    defaults: {
        show: false,
        showInfo: false
    },
    init: function(options) {
        var
        // variables
        self = this,
        lightbox = self._lightbox,
        // functions
        setActive = function (index) {
            self._carousel.follow(index);
            $( self._thumbnails[index].container )
                .addClass( 'active' )
                .siblings( '.active' )
                .removeClass( 'active' );
        };
        self.$("thumbnails").children().bind(Galleria.TOUCH ? "mouseup": "click", function() {
            var active = $(this).data("index");
            self.openLightbox(active);
            setActive(active);
        });
        // override lightbox.showNext
        lightbox.showNext = function() {
            lightbox.show( self.getNext( lightbox.active ) );
            setActive(lightbox.active);
        }
        // override lightbox.showPrev
        lightbox.showPrev = function() {
            lightbox.show( self.getPrev( lightbox.active ) );
            setActive(lightbox.active);
        }
    }
});